import React, {useEffect, useState} from 'react'
import { useParams } from 'react-router-dom';
// import notes from '../assets/data';
import { Link } from 'react-router-dom';
import { ReactComponent as ArrowLeft } from '../assets/arrow-left.svg'

const NotePage = ({history}) => {
 // let {id} = useParams();
  let noteId= useParams().id;


  // useParams() Returns an object of key/value pairs of the dynamic params from the current URL that were matched by the route path.
  //console.log(props);
  //console.log(useParams);
   console.log(noteId);


  // let note = notes.find(note => note.id === Number(noteId));

  let [note, setNote] = useState (null)
  // useState should be set at the top level of the component and never inside the useEffect

  useEffect(() => {
    getNote()
  }, [noteId])

  let getNote = async () => {
    let response = await fetch (`http://localhost:8000/notes/${noteId}`)
      console.log(response)
    let data =  await response.json()
      console.log(data)
    setNote(data)
  }

  console.log(note)

   let updateNote = async () => {
       await fetch(`http://localhost:8000/notes/${noteId}`, {
         method: 'PUT',
         headers: {
              'Content-type': 'application/json'
         },
         body: JSON.stringify({...note, 'updated': new Date()})

       })
   }

   let deleteNote = async () => {
        await fetch(`http://localhost:8000/notes/${noteId}`, {
          method: 'DELETE',
          headers: {
              'Content-type': 'application/json'
          },
          body: JSON.stringify(note)

        })
        history.go('/')
   }

   let handleSubmit = () => {
       updateNote ()
       history.go('/')
   }

  return (
    <div className='note'>

      <div className='note-header'>
         <h3>
            <Link to=''>
              <ArrowLeft onClick={handleSubmit}/>

            </Link>

         </h3>
         <button onClick = {deleteNote}>Delete</button>

      </div>
      <textarea onChange ={(e) => {setNote({...note, 'body':e.target.value})}}  value= {note?.body} >
      {/* // if there is... */}
        </textarea>
    </div>
  )
}

export default NotePage
